import React, { useState } from 'react'
import { Card } from 'antd';
import styled from 'styled-components';

const PoastDiv = styled.div`
    width:200px;
    height:30px;
    // background:red;
    display:flex;
    flex-wrap:wrap;
`;
const TextDiv = styled.div`
    width:200px;
    height:5px;
    // background:blue;
    text-align:start;
    font-size:1rem;
    // padding-bottom:15px;
`;
const PriceDiv = styled.div`
    height:4px;
    width:100px;
    // background:yellow;
    flex-wrap:wrap;
    padding-top:40px;
    color:#a7a7a7;
    font-weight:600;
`;
const TimeDiv = styled(PriceDiv)`
    text-align:end;
    font-weight:400;
`;
const AllPostDiv = styled.div`
    width:700px;
    display:flex;
    // background:red;
    flex-wrap:wrap;
`;

function PostCard2() {
    const [post,setPost] = useState([
        {id:"asd1321",title:"육각렌치 빌려주세요~",time:"방금 전",렌탈요금:"협의",img:"./img/logo2.png"}
        ,{id:"asd1322",title:"담배 빌려주세요~",time:"1시간 전",렌탈요금:"400원"}
        ,{id:"asd1323",title:"라이터 빌려주세요~",time:"2시간 전",렌탈요금:"500원"}

])
    const Allpost = post.map((item,index)=>(
        <div style={{padding:"30px"}}>
        <Card
    hoverable
    style={{ width: "244px"}}
    cover={<img alt="example" src={item.img ? item.img : "https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png"} style={{width:"243px",height:"198px"}}/>}
    >
    <PoastDiv><TextDiv>{item.title}</TextDiv><PriceDiv >{item.렌탈요금}</PriceDiv><TimeDiv>{item.time}</TimeDiv></PoastDiv>
  </Card></div>));
    return (
        <>
        <AllPostDiv>
            {Allpost}
        </AllPostDiv>
        </>
    )
}

export default PostCard2
