import React, {useState} from 'react'
import styled, {createGlobalStyle} from 'styled-components'
import {
  UserOutlined,
  MailOutlined,
  SearchOutlined,
  FormOutlined,
  MenuOutlined,
  CloseOutlined,
  DownOutlined
} from '@ant-design/icons';
import {MenuItems} from './MenuItems';
import {Menu, Dropdown, Row, Col} from 'antd';
import logo from './logo2.png';
import Link from 'next/link';
import Navbar from 'react-bootstrap/Navbar'

const Topbar = styled.div`
  padding: 1%;
  width: 100%;
  height: 40px;
  font-size: 0.6em;
  background: red;
  border-bottom: solid #eeeeee;
  display: flex;
  justify-content: center;;
  background: RGB(255, 255, 255);
  padding: 0 35px;
`;
const TopDiv = styled.div`
  padding: 13px;
  display: flex;
  justify-content: flex-end;
  width: 80%;
  backgounr: blue;
  font-size: 0.9em;
  color: RGB(127, 127, 127);
`;

const NavBar = styled.div`
  width: 100%;
  // background:red;
  height: 146px;
  display: flex;
  // background:yellow;
  border-bottom: solid #eeeeee;
  padding: 0 35px;
  align-items: center;
  flex-wrap: wrap;
`;
const NavBarDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  align-content: center;
  align-items: center;
  // background:blue;
  padding: 49px;
  justify-content: center;
`;
const GlobalStyle = createGlobalStyle`
  div {
    cursor: pointer;
  }

  input:focus {
    outline: none;
  }

  ul {
    list-style: none;
  }
`;
const SelcectDiv = styled.div`
  color: #212a4b;
  width: 450px;
  height: 30px;
  border: solid #212a4b;
  min-width: 450px;
  display: flex;
`;
const SelectDropD = styled.div`
  width: 60px;
  hieght: 30px;
  // background:red;
  padding-top: 4px;
  font-size: 13px;
  font-weight: 600;
  border-right: solid #212a4b;
  align-items: center;
  padding-left: 5px;
  min-width: 60px;
  a{
    color:black;
  }
  a:hover {
    color: black;
  }
`;
const Select = styled.input`
  padding: 1.5%;
  width: 250px;
  hieght: 30px;
  border: none;
  float: left;
  // padding-left:80px;

`;
const ProfileDiv = styled.div`
  width: 400px;
  height: 28px;
  // background:white;
  display: flex;
  padding-left: 50px;
`;
const UserDiv = styled.div`
  width: 120px;
  height: 26px;
  border-right: solid #e6e6e6;
  padding-top: 2px;
  padding-left: 28px;
`;
const NavMenu = styled.ul`
  flex-direction: column;
  width: 100%;
  position: absolute;
  // top: 80px;
  // left:-100%;
  transition: all .5s ease;
`;
const NavActive = styled.ul`
  position: absolute;
  z-index: 1;

`;
const MenuLi = styled.li`
  text-align:center;
  padding-bottom:40px;
  font-weight:600;
  font-size:20px;
  transition: all 0.25s ease;
  :hover {
    transform: scale(1.3, 1.3);
  }
  :first-child{
    padding-top:20px;
  }
  a{
    color:black;
  }
  a:hover{
    color:black;
  }

`;
const MenuDiv = styled.div`
  width: 200px;
  flex-wrap: wrap;
  padding-top: 30px;
  min-width: 200px;
`;
const LogoDiv = styled.div`
  width: 150px;
  position: absolute;
  padding-top: 25px;
  min-height: 50px;
`;
const MenuA = styled.div`
