import React, {useState} from 'react'
import styled, {createGlobalStyle} from 'styled-components'
import {
  UserOutlined,
  MailOutlined,
  SearchOutlined,
  FormOutlined,
  MenuOutlined,
  CloseOutlined,
  DownOutlined
} from '@ant-design/icons';
import {MenuItems} from './MenuItems';
import {Menu, Dropdown, Row, Col} from 'antd';
import logo from './logo2.png';
import Link from 'next/link';

const Topbar = styled.div`
  padding: 1%;
  width: 100%;
  height: 40px;
  border: solid;
  font-size: 0.6em;
  background: red;
  border-bottom: solid #eeeeee;
  display: flex;
  justify-content: center;;
  background: RGB(255, 255, 255);
  padding: 0 35px;
`;
const TopDiv = styled.div`
  padding: 13px;
  display: flex;
  justify-content: flex-end;
  width: 80%;
  backgounr: blue;
  font-size: 0.9em;
  color: RGB(127, 127, 127);
`;

const NavBar = styled.div`
  width: 100%;
  // background:red;
  height: 146px;
  display: flex;
  // background:yellow;
  border-bottom: solid #eeeeee;
  padding: 0 35px;
  align-items: center;
  flex-wrap: wrap;
`;
const NavBarDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  align-content: center;
  align-items: center;
  // background:blue;
  padding: 49px;
  justify-content: center;
`;
const GlobalStyle = createGlobalStyle`
  div {
    cursor: pointer;
  }

  input:focus {
    outline: none;
  }

  ul {
    list-style: none;
  }

  a {
    color: black;
  }

  a:hover {
    color: black;
  }
`;
const SelcectDiv = styled.div`
  color: #212a4b;
  width: 450px;
  height: 30px;
  border: solid #212a4b;
  min-width: 450px;
  display: flex;
`;
const SelectDropD = styled.div`
  width: 60px;
  hieght: 30px;
  // background:red;
  padding-top: 4px;
  font-size: 13px;
  font-weight: 600;
  border-right: solid #212a4b;
  align-items: center;
  padding-left: 5px;
  min-width: 60px;
`;
const Select = styled.input`
  padding: 1.5%;
  width: 250px;
  hieght: 30px;
  border: none;
  float: left;
  // padding-left:80px;

`;
const ProfileDiv = styled.div`
  width: 400px;
  height: 28px;
  // background:white;
  display: flex;
  padding-left: 50px;
`;
const UserDiv = styled.div`
  width: 120px;
  height: 26px;
  border-right: solid #e6e6e6;
  padding-top: 2px;
  padding-left: 28px;
`;
const NavMenu = styled.ul`
  flex-direction: column;
  width: 100%;
  position: absolute;
  // top: 80px;
  // left:-100%;
  transition: all .5s ease;
`;
const NavActive = styled.ul`
  position: absolute;
  z-index: 1;

`;
const MenuLi = styled.li`
  text-align:center;
  padding-bottom:40px;
  font-weight:600;
  font-size:20px;
  transition: all 0.25s ease;
  :hover {
    transform: scale(1.3, 1.3);
  }
  :first-child{
    padding-top:20px;
  }

`;
const MenuDiv = styled.div`
  width: 200px;
  flex-wrap: wrap;
  padding-top: 30px;
  min-width: 200px;
`;
const LogoDiv = styled.div`
  width: 150px;
  position: absolute;
  padding-top: 25px;
  min-height: 50px;
`;
const MenuA = styled.div`
  width: 30px;
  padding-top: 90px;
  // minwidth:120px;
`;

function Layout({children}) {
  const [isOpen, setMenu] = useState(false);
  const [profile, SetProfile] = useState(false);
  const [select, SetSelect] = useState("선택");
  const toggleMenu = () => {
    setMenu(!isOpen);
  }
  const goProfile = () => {
    SetProfile(true);
    console.log(profile);
  }
  console.log(select);
  const menu = (
    <Menu>
      <Menu.Item key="0">
        <a onClick={() => {
          SetSelect("닉네임")
        }}>닉네임</a>
      </Menu.Item>
      <Menu.Item key="1">
        <a onClick={() => {
          SetSelect("글제목")
        }}>글제목</a>
      </Menu.Item>
      <Menu.Item key="2">
        <a onClick={() => {
          SetSelect("글내용")
        }}>글내용</a>
      </Menu.Item>
    </Menu>
  )
  return (
    <div style={{position: "fixed", width: "100%"}}>
      <GlobalStyle/>
      {/*<div>*/}
        <Topbar>
          <TopDiv>
            <div>
              로그인/회원가입
            </div>
            <div style={{paddingLeft: "20px"}}>
              내상점
            </div>
          </TopDiv>
        </Topbar>
        <NavBar>
          <NavBarDiv>
            <MenuDiv>
              <LogoDiv>
                <img src={logo} width="200px"
                     style={{float: "left", paddingBottom: "10px", paddingRight: "50px"}}/>
              </LogoDiv>
              <MenuA>
                {!isOpen ?
                  <MenuOutlined style={{fontSize: "20px"}} onClick={toggleMenu}/>
                  :
                  <CloseOutlined style={{fontSize: "20px"}} onClick={toggleMenu}/>
                }
                {isOpen ?
                  <NavActive>
                    {MenuItems.map((item, index) => {
                      return (
                        <>
                        {index === 0 &&<Link href="/objectrecieve"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        {index === 1 &&<Link href="/objectsend"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        {index === 2 &&<Link href="/talentrecieve"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        {index === 3 &&<Link href="/talentsend"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        {index === 4 &&<Link href="/cooperate"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        {index === 5 &&<Link href="/playground"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                        </>
                      )
                    })}
                  </NavActive> : <NavMenu/>
                }
              </MenuA>
            </MenuDiv>
            <SelcectDiv>
              <SelectDropD>
                <Dropdown overlay={menu} trigger={['click']}>
                  <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {select} <DownOutlined/>
                  </a>
                </Dropdown>
              </SelectDropD>
              <Select placeholder={"지역, 상품명 입력"}/>
              <div style={{paddingTop: "3px", paddingLeft: "115px"}}>
                <SearchOutlined/>
              </div>
            </SelcectDiv>
            <ProfileDiv>
              <UserDiv onClick={goProfile}>
                <UserOutlined/> 내프로필
              </UserDiv>
              <UserDiv>
                <MailOutlined/> 알림톡
              </UserDiv>
              <UserDiv>
                <FormOutlined/> 글 쓰기
